import {data} from "./data"
export const requestForData = (dataType) => new Promise(resolve => {
    setTimeout(() => {
        resolve({ tableData: data[dataType].tableData, column: data[dataType].column })
    }, 1000);
});

export const fetchCustomerData = async (customerId) => {
    const url = 'http://localhost/dtb_project_php/listing/customer/customerdata.php'
    const params = new URLSearchParams({ id: customerId });
    const response = await fetch(url, {
        method: 'POST',
        body: params
    });
    const data = await response.json();
    return data;
}
export const fetchProviderData = async (providerId) => {
    const url = 'http://localhost/dtb_project_php/listing/provider/customerdata.php'
    const params = new URLSearchParams({ id: providerId });
    const response = await fetch(url, {
        method: 'POST',
        body: params
    });
    const data = await response.json();
    return data;
}
export const fetchData = async (dataType, uid) => {
    const url = {
        inventory: 'http://localhost/dtb_project_php/listing/product/inventory.php',
        customer: 'http://localhost/dtb_project_php/listing/customer/customer.php',
        orderFromCustomer: 'http://localhost/dtb_project_php/listing/customer/orderFromCustomer.php',
        provider: 'http://localhost/dtb_project_php/listing/provider/provider.php',     
        importFromProvider: 'http://localhost/dtb_project_php/listing/provider/importFromProvider.php',
