        importDetail: 'http://localhost/dtb_project_php/listing/import/importDetail.php',
        order: 'http://localhost/dtb_project_php/listing/order/order.php',
        orderDetail: 'http://localhost/dtb_project_php/listing/order/orderDetail.php',
    }
    const params = new URLSearchParams({ id: uid });
    const response = await fetch(url[dataType], {
        method: 'POST',
        body: params
    });
    const data = response.json();
    return data
}

